  var hc1Element = document.getElementById('hc1');
  var h2Element = document.getElementById('h2');
  var hc2Element = document.getElementById('hc2');
  var h3Element = document.getElementById('h3');
  var hc3Element = document.getElementById('hc3');
  var h4Element = document.getElementById('h4');
  var hc4Element = document.getElementById('hc4');

  h1Element.addEventListener('click', function() {
    hc1Element.style.display = hc1Element.style.display === 'block' ? 'none' : 'block';
